describe HuntersController do

  # This should return the minimal set of attributes required to create a valid
  # Hunter. As you add validations to Hunter, be sure to
  # update the return value of this method accordingly.
  def valid_attributes
    {}
  end

  describe "GET index" do
    it "assigns all hunters as @hunters" do
      hunter = Hunter.create! valid_attributes
      get :index
      assigns(:hunters).should eq([hunter])
    end
  end

  describe "GET show" do
    it "assigns the requested hunter as @hunter" do
      hunter = Hunter.create! valid_attributes
      get :show, :id => hunter.id.to_s
      assigns(:hunter).should eq(hunter)
    end
  end

  describe "GET new" do
    it "assigns a new hunter as @hunter" do
      get :new
      assigns(:hunter).should be_a_new(Hunter)
    end
  end

  describe "GET edit" do
    it "assigns the requested hunter as @hunter" do
      hunter = Hunter.create! valid_attributes
      get :edit, :id => hunter.id.to_s
      assigns(:hunter).should eq(hunter)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "creates a new Hunter" do
        expect {
          post :create, :hunter => valid_attributes
        }.to change(Hunter, :count).by(1)
      end

      it "assigns a newly created hunter as @hunter" do
        post :create, :hunter => valid_attributes
        assigns(:hunter).should be_a(Hunter)
        assigns(:hunter).should be_persisted
      end

      it "redirects to the created hunter" do
        post :create, :hunter => valid_attributes
        response.should redirect_to(Hunter.last)
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved hunter as @hunter" do
        # Trigger the behavior that occurs when invalid params are submitted
        Hunter.any_instance.stub(:save).and_return(false)
        post :create, :hunter => {}
        assigns(:hunter).should be_a_new(Hunter)
      end

      it "re-renders the 'new' template" do
        # Trigger the behavior that occurs when invalid params are submitted
        Hunter.any_instance.stub(:save).and_return(false)
        post :create, :hunter => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested hunter" do
        hunter = Hunter.create! valid_attributes
        # Assuming there are no other hunters in the database, this
        # specifies that the Hunter created on the previous line
        # receives the :update_attributes message with whatever params are
