        put :update, :id => hunter.id, :hunter => valid_attributes
        assigns(:hunter).should eq(hunter)
      end

      it "redirects to the hunter" do
        hunter = Hunter.create! valid_attributes
        put :update, :id => hunter.id, :hunter => valid_attributes
        response.should redirect_to(hunter)
